mapper.update:
    UPDATE user_relations
	SET regular_friend=#{regularFriend}
	WHERE user_id1=#{user1} and user_id2=#{user2};

    UPDATE user_relations
	SET friend_circle1=#{friendCircle1}
	WHERE user_id1=#{user1} and user_id2=#{user2};

    UPDATE user_relations
	SET friend_circle2=#{friendCircle2}
	WHERE user_id1=#{user1} and user_id2=#{user2};

    UPDATE user_relations
	SET friend_map=#{friendMap}
	WHERE user_id1=#{user1} and user_id2=#{user2};

UPDATE user_info
SET approve_type=#{approveTypeSwitch}
WHERE user_id=#{userId};

UPDATE user_info
SET show_in_map=#{showInMap}
WHERE user_id=#{userId};

UPDATE user_info
SET pic_view_limit=#{picViewLimit}
WHERE user_id=#{userId};
